import { Injectable } from '@angular/core';
import { AngularFirestore } from '@angular/fire/compat/firestore';
import { Observable, throwError, switchMap, catchError } from 'rxjs';
import { AuthService } from './auth.service';
import { FirebaseBook } from '../models/Book';
import { Chapter, FirebaseChapter } from '../models/Chapter';
import { FirebaseUser } from '../models/User';

@Injectable({
  providedIn: 'root'
})
export class FirestoreService {

  constructor(
    private firestore: AngularFirestore,
    private authService: AuthService
  ) { }

  private getCurrentUserId(): Observable<string | null> {
    return this.authService.getCurrentUserId();
  }

  createBook(title: string, imageUrl?: string): Observable<void> {
    return this.getCurrentUserId().pipe(
      switchMap(userId => {
        if (!userId) throw new Error("User not authenticated");
        const bookRef = this.firestore.collection<FirebaseBook>(`users/${userId}/books`).doc();
        return bookRef.set({ title, chaptersInfo: [] });
      }),
      catchError(error => throwError(() => error))
    );
  }

  createChapter(bookId: string, title: string, content: string): Observable<void> {
    return this.getCurrentUserId().pipe(
      switchMap(userId => {
        if (!userId) throw new Error("User not authenticated");
        const chapterRef = this.firestore.collection<FirebaseChapter>(`users/${userId}/books/${bookId}/chapters`).doc();
        return chapterRef.set({ title, content });
      }),
      catchError(error => throwError(() => error))
    );
  }

  fetchBook(bookId: string): Observable<FirebaseBook | undefined> {
    return this.getCurrentUserId().pipe(
      switchMap(userId => {
        if (!userId) throw new Error("User not authenticated");
        return this.firestore.doc<FirebaseBook>(`users/${userId}/books/${bookId}`).valueChanges();
      }),
      catchError(error => throwError(() => error))
    );
  }

  fetchChapter(bookId: string, chapterId: string): Observable<FirebaseChapter | undefined> {
    return this.getCurrentUserId().pipe(
      switchMap(userId => {
        if (!userId) throw new Error("User not authenticated");
        return this.firestore.doc<FirebaseChapter>(`users/${userId}/books/${bookId}/chapters/${chapterId}`).valueChanges();
      }),
      catchError(error => throwError(() => error))
    );
  }

  fetchUserData(): Observable<FirebaseUser | undefined> {
    return this.getCurrentUserId().pipe(
      switchMap(userId => {
        if (!userId) throw new Error("User not authenticated");
        return this.firestore.doc<FirebaseUser>(`users/${userId}`).valueChanges();
      }),
      catchError(error => throwError(() => error))
    );
  }

  writeBook(bookId: string, title: string, imageUrl?: string): Observable<void> {
    return this.getCurrentUserId().pipe(
      switchMap(userId => {
        if (!userId) throw new Error("User not authenticated");
        return this.firestore.doc(`users/${userId}/books/${bookId}`).set({ title, imageUrl: imageUrl || null }, { merge: true });
      }),
      catchError(error => throwError(() => error))
    );
  }

  writeChapter(bookId: string, chapterId: string, title: string, content: string): Observable<void> {
    return this.getCurrentUserId().pipe(
      switchMap(userId => {
        if (!userId) throw new Error("User not authenticated");
        return this.firestore.doc(`users/${userId}/books/${bookId}/chapters/${chapterId}`).set({ title, content }, { merge: true });
      }),
      catchError(error => throwError(() => error))
    );
  }

  deleteBook(bookId: string): Observable<void> {
    return this.getCurrentUserId().pipe(
      switchMap(userId => {
        if (!userId) throw new Error("User not authenticated");
        return this.firestore.doc(`users/${userId}/books/${bookId}`).delete();
      }),
      catchError(error => throwError(() => error))
    );
  }

  deleteChapter(bookId: string, chapterId: string): Observable<void> {
    return this.getCurrentUserId().pipe(
      switchMap(userId => {
        if (!userId) throw new Error("User not authenticated");
        return this.firestore.doc(`users/${userId}/books/${bookId}/chapters/${chapterId}`).delete();
      }),
      catchError(error => throwError(() => error))
    );
  }
  updateChapterOrder(bookId: string, chapters: Chapter[]): Observable<void> {
    return this.getCurrentUserId().pipe(
      switchMap(userId => {
        if (!userId) throw new Error("User not authenticated");
        const bookRef = this.firestore.doc<FirebaseBook>(`users/${userId}/books/${bookId}`);
        return bookRef.update({ chaptersInfo: chapters });
      }),
      catchError(error => throwError(() => error))
    );
  }
  updateChapterTitle(bookId: string, chapterId: string, newTitle: string): Observable<void> {
    return this.getCurrentUserId().pipe(
      switchMap(userId => {
        if (!userId) throw new Error("User not authenticated");
        const chapterRef = this.firestore.doc(`users/${userId}/books/${bookId}/chapters/${chapterId}`);
        return chapterRef.update({ title: newTitle });
      }),
      catchError(error => throwError(() => error))
    );
  }
}

import { Injectable, Inject, PLATFORM_ID } from '@angular/core';
import { isPlatformBrowser } from '@angular/common';

@Injectable({
  providedIn: 'root'
})
export class LocalStorageService {

  constructor(@Inject(PLATFORM_ID) private platformId: Object) { }

  setItem(key: string, value: any): void {
    if (isPlatformBrowser(this.platformId)) {
      localStorage.setItem(key, JSON.stringify(value));
    }
  }

  getItem(key: string): any {
    if (isPlatformBrowser(this.platformId)) {
      const item = localStorage.getItem(key);
      return item ? JSON.parse(item) : null;
    }
    return null;
  }

  removeItem(key: string): void {
    if (isPlatformBrowser(this.platformId)) {
      localStorage.removeItem(key);
    }
  }
}

import { Injectable, Inject, PLATFORM_ID } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable, Subject } from 'rxjs';
import { isPlatformBrowser } from '@angular/common';
@Injectable({
  providedIn: 'root'
})
export class TextGenerationService {
  private apiUrl = 'http://127.0.0.1:1234';
  private eventSource: EventSource | null = null;
  private messageSubject = new Subject<any>();

  constructor(private http: HttpClient, @Inject(PLATFORM_ID) private platformId: Object) { }

  getCompletions(prompt: string) {
    return this.http.post(`${this.apiUrl}/v1/completions`, {
      prompt: prompt,
      max_tokens: 200,
      temperature: 1,
      top_p: 0.9,
      seed: 10,
    });
  }

  setupSseConnection(): Observable<any> {
    return new Observable(observer => {
      this.eventSource = new EventSource(`${this.apiUrl}/v1/chat/completions`);

      this.eventSource.onmessage = event => {
        observer.next(event.data);
      };

      this.eventSource.onerror = error => {
        observer.error(error);
      };

      return () => {
        this?.eventSource?.close();
      };
    });
  }

  getChatCompletions(message: string) {
    return this.http.post(`${this.apiUrl}/v1/chat/completions`, {
      messages: [{ role: 'user', content: message }],
      mode: 'instruct',
      instruction_template: 'Alpaca',
      stream: false,
    });
  }

  getMessages(): Observable<any> | null {
    if (isPlatformBrowser(this.platformId)) {
      return this.messageSubject.asObservable();
    } else {
      return null;
    }
  }
}

import { Injectable, Inject, PLATFORM_ID } from '@angular/core';
import { isPlatformBrowser } from '@angular/common';
import { LocalStorageService } from './storage-service.service';

export const THEMES = ['light', 'dark', 'focus'];
export type Theme = 'light' | 'dark' | 'focus';

@Injectable({ providedIn: 'root' })
export class ThemeService {
  private theme: Theme = 'light';

  constructor(
    private localStorage: LocalStorageService,
    @Inject(PLATFORM_ID) private platformId: Object
  ) {
    this.initializeTheme();
  }

  private initializeTheme(): void {
    // Safely retrieve the theme from local storage
    let savedTheme: string | null;
    try {
      savedTheme = this.localStorage.getItem('theme');
      if (savedTheme && THEMES.includes(savedTheme)) {
        this.setTheme(savedTheme as Theme);
      } else {
        this.setTheme('light');
      }
    } catch (error) {
      console.error('Error reading theme from local storage:', error);
      this.setTheme('light'); // Fallback to default theme in case of error
    }
  }

  setTheme(theme: Theme): void {
    this.theme = theme;
    this.localStorage.setItem('theme', theme);
    if (isPlatformBrowser(this.platformId)) {
      document.documentElement.setAttribute('data-theme', theme);
    }
  }

  getCurrentTheme(): Theme {
    return this.theme;
  }
}

