import { EventEmitter, Injectable } from '@angular/core';
import { AngularFireAuth } from '@angular/fire/compat/auth';
import firebase from 'firebase/compat/app';
import { LocalStorageService } from './storage-service.service';
@Injectable({ providedIn: 'root' })
export class AuthService {
  isLoggedIn: boolean = false;
  onAuthStateChanged: EventEmitter<boolean> = new EventEmitter();
  constructor(public afAuth: AngularFireAuth, private localStorage: LocalStorageService) {
    this.isLoggedIn = this.localStorage.getItem('user')
    this.afAuth.authState.subscribe(
      (user) => {
        if (user) {
          this.localStorage.setItem('user', true);
          this.isLoggedIn = true;
        } else {
          this.localStorage.setItem('user', false);
          this.isLoggedIn = false;
        }
      }
    );

  }
  async signInWithGoogle() {
    const provider = new firebase.auth.GoogleAuthProvider();
    try {
      const result = await this.afAuth.signInWithPopup(provider);
      return result;
    } catch (error) {
      console.error(error);
      return null;
    }
    this.onAuthStateChanged.emit(true); // Emit event after login
  }
  async signOutGoogle() {
    await this.afAuth.signOut()
    this.onAuthStateChanged.emit(false); // Emit event after logout
  }
}

import { Injectable, Inject, PLATFORM_ID } from '@angular/core';
import { isPlatformBrowser } from '@angular/common';

@Injectable({
  providedIn: 'root'
})
export class LocalStorageService {

  constructor(@Inject(PLATFORM_ID) private platformId: Object) { }

  setItem(key: string, value: any): void {
    if (isPlatformBrowser(this.platformId)) {
      localStorage.setItem(key, JSON.stringify(value));
    }
  }

  getItem(key: string): any {
    if (isPlatformBrowser(this.platformId)) {
      const item = localStorage.getItem(key);
      return item ? JSON.parse(item) : null;
    }
    return null;
  }

  removeItem(key: string): void {
    if (isPlatformBrowser(this.platformId)) {
      localStorage.removeItem(key);
    }
  }
}

